    "noImplicitAny": true,
    "noImplicitThis": null,
    "allowJs": false,
    "allowImportingTsExtensions": false,
    "resolvePackageJsonExports": false,
    "sourceMap": true,
    "noEmit": true,
    "baseUrl": ".",
    "paths": {
      "@/*": ["./src/*"],
