{
  "presets": [
      ["env", {
          "targets": {
              "modules": false,
              "loose": true,
              "browsers": ["> 1%", "last 2 versions", "not ie <= 8"]
          }
      }],
      "stage-0"
  ],
  "plugins": [
    ["import",[{
        "libraryName": "weex-ui",
        "libraryDirectory": "packages",
        "style": false
    }, {
        "libraryName": "bui-weex",
        "libraryDirectory": "src/components",
        "style": false
    }]],
    ["transform-runtime", {
      "polyfill": false,
      "helpers": false
    }]
  ],
  "env": {
      "test": {
          "presets": ["env", "stage-0"],
          "plugins": ["istanbul"]
      }
  }
}

{
  "env": {
    "es6": true,
    "node": true,
    "mocha": true,
    "browser": true
  },
  "plugins": [
    "html"
  ],
  "parserOptions": {
    "sourceType": "module"
  },
  "globals": {
    "weex": true,
    "Vue": true
  },
  "rules": {
    "semi": "off",
    "indent": 0,
    "quotes": [ "error", "single" ],
    "no-control-regex": 0,
    "no-extend-native": 0,
    "handle-callback-err": 0,
    "camelcase": 0,
    "no-return-assign": [ 2, "except-parens" ]
  },
  "extends": [
    "vue"
  ],
  "parser": "babel-eslint"
}
