{
    "root": true,
    "env": {
        "browser": true,
        "es6": true,
        "node": true
    },
    "extends": ["plugin:jsonc/base", "plugin:vue/recommended", "eslint:recommended", "@vue/typescript", "prettier"],
    "globals": {
        "Atomics": "readonly",
        "SharedArrayBuffer": "readonly"
    },
    "rules": {
        "vue/no-v-html": 0,
        "vue/component-tags-order": 0,
        "vue/no-v-text-v-html-on-component": 0,
        "vue/valid-v-slot": [
            "error",
            {
                "allowModifiers": true
            }
        ]
    },
    "parser": "vue-eslint-parser",
    "parserOptions": {
        "parser": "@typescript-eslint/parser",
        "sourceType": "module"
    },
    "plugins": ["vue"],
    "overrides": [
        {
            "files": "src/locales/*.json",
            "parser": "jsonc-eslint-parser",
            "rules": {
                "jsonc/no-dupe-keys": "error",
                "jsonc/sort-keys": ["error", "asc", { "caseSensitive": false, "natural": false }]
            }
        }
    ]
}

{
    "tabWidth": 4,
    "singleQuote": true,
    "semi": false,
    "printWidth": 120,
    "bracketSameLine": true,
    "trailingComma": "es5",
    "htmlWhitespaceSensitivity": "ignore",
    "overrides": [
        {
            "files": "*.yml",
            "options": {
                "tabWidth": 2
            }
        },
        {
            "files": "*.md",
            "options": {
                "tabWidth": 2
            }
        },
        {
            "files": "src/locales/*.json",
            "options": {
                "plugins": ["prettier-plugin-sort-json"],
                "jsonRecursiveSort": true,
                "jsonSortOrder": "{ \"placeThisFirst\": null, \"/^[a-zA-Z0-9]/\": \"caseInsensitiveNumeric\"  }"
            }
        }
    ]
}

import { defineConfig } from 'cypress'

export default defineConfig({
    e2e: {
        // We've imported your old cypress plugins here.
        // You may want to clean this up later by importing these.
        setupNodeEvents(on, config) {
            // @ts-ignore
            return require('./cypress/plugins/index.js')(on, config)
        },
        baseUrl: 'http://localhost:4173',
    },
})

