        // Static
        "theme-success": colors.emerald[600],
        "theme-danger": colors.rose[600],
      },
    },
  },
  plugins: [],
};

import { defineConfig } from "vite";
import vue from "@vitejs/plugin-vue";

const devApiUrl = "http://127.0.0.1:8000";

export default defineConfig({
