module.exports = {
  parser: 'vue-eslint-parser',
  globals: { defineOptions: 'writable' },

  parserOptions: {
    parser: '@typescript-eslint/parser',
    ecmaVersion: 2020,
    sourceType: 'module',
    ecmaFeatures: {
      jsx: true
    }
  },

  extends: [
    'plugin:vue/vue3-recommended',
    'plugin:@typescript-eslint/recommended',
    'vue-global-api',
    './.eslintrc-auto-import.json',
    'prettier',
    'plugin:prettier/recommended'
  ],

  rules: {
    '@typescript-eslint/ban-ts-ignore': 'off',
    '@typescript-eslint/explicit-function-return-type': 'off',
    '@typescript-eslint/no-explicit-any': 'off',
    '@typescript-eslint/no-var-requires': 'off',
    '@typescript-eslint/no-empty-function': 'off',
    'vue/custom-event-name-casing': 'off',
    'no-use-before-define': 'off',
    '@typescript-eslint/no-use-before-define': 'off',
    '@typescript-eslint/ban-ts-comment': 'off',
    '@typescript-eslint/ban-types': 'off',
    '@typescript-eslint/no-non-null-assertion': 'off',
    '@typescript-eslint/explicit-module-boundary-types': 'off',
    '@typescript-eslint/no-unused-vars': [
      'error',
      {
        argsIgnorePattern: '^h$',
        varsIgnorePattern: '^h$'
      }
    ],
    'vue/multi-word-component-names': 'off',
    'no-unused-vars': [
      'error',
      {
        argsIgnorePattern: '^h$',
        varsIgnorePattern: '^h$'
      }
    ],
    'space-before-function-paren': 'off',
    quotes: ['error', 'single'],
    'comma-dangle': ['error', 'never']
  }
};

module.exports = {
  '*.{js,ts,vue}': 'pnpm eslint',
  '*.vue': [
    () => {
      return `vue-tsc --noEmit --skipLibCheck`
    },
  ],
}

module.exports = {
  extends: ['@commitlint/config-conventional', 'cz'],
  rules: {
    'type-enum': [
      2,
      'always',
      [
        'feature', // 新功能（feature）
        'bug', // 此项特别针对bug号，用于向测试反馈bug列表的bug修改情况
        'fix', // 修补bug
        'ui', // 更新 ui
        'docs', // 文档（documentation）
        'style', // 格式（不影响代码运行的变动）
        'perf', // 性能优化
        'release', // 发布
        'deploy', // 部署
        'refactor', // 重构（即不是新增功能，也不是修改bug的代码变动）
        'test', // 增加测试
        'chore', // 构建过程或辅助工具的变动
        'revert', // feat(pencil): add ‘graphiteWidth’ option (撤销之前的commit)
        'merge', // 合并分支， 例如： merge（前端页面）： feature-xxxx修改线程地址
        'build' // 打包
      ]
    ],
    // <type> 格式 小写
    'type-case': [2, 'always', 'lower-case'],
    // <type> 不能为空
    'type-empty': [2, 'never'],
    // <scope> 范围不能为空
    'scope-empty': [2, 'never'],
    // <scope> 范围格式
    'scope-case': [0],
    // <subject> 主要 message 不能为空
    'subject-empty': [2, 'never'],
    // <subject> 以什么为结束标志，禁用
    'subject-full-stop': [0, 'never'],
    // <subject> 格式，禁用
    'subject-case': [0, 'never'],
    // <body> 以空行开头
    'body-leading-blank': [1, 'always'],
    'header-max-length': [0, 'always', 72]
  }
};

events {
    worker_connections 1024;
}

http {
    include /etc/nginx/mime.types;
    default_type application/octet-stream;

    # 增加 client_max_body_size 指令，设置请求体的最大大小为50M
    client_max_body_size 50M;

    # 新增重定向 www.91huajian.cn 到 maobucv.com
    server {
        listen 80;
        server_name www.91huajian.cn;

        return 301 https://maobucv.com$request_uri;
    }

    server {
        listen 443 ssl;
        server_name www.91huajian.cn;

        ssl_certificate /etc/nginx/ssl/fullchain.crt;
        ssl_certificate_key /etc/nginx/ssl/private.pem;

        return 301 https://maobucv.com$request_uri;
    }

    # 新增重定向 91huajian.cn 到 maobucv.com
    server {
        listen 80;
        server_name 91huajian.cn;

        return 301 https://maobucv.com$request_uri;
    }

    server {
        listen 443 ssl http2;
        server_name 91huajian.cn;

        ssl_certificate /etc/nginx/ssl/fullchain.crt;
        ssl_certificate_key /etc/nginx/ssl/private.pem;

        return 301 https://maobucv.com$request_uri;
    }

    # 新增重定向 www.maobucv.com 到 maobucv.com
    server {
        listen 80;
        server_name www.maobucv.com;

        return 301 https://maobucv.com$request_uri;
    }

    server {
        listen 443 ssl;
        server_name www.maobucv.com;

        ssl_certificate /etc/nginx/ssl/maobucv.crt;
        ssl_certificate_key /etc/nginx/ssl/maobucv.pem;

        return 301 https://maobucv.com$request_uri;
    }

    # 处理 maobucv.com 的 HTTP 请求
    server {
        listen 80;
        server_name maobucv.com;

        location / {
            root /usr/share/nginx/html;
            index index.html index.htm;
            try_files $uri $uri/ /index.html;
        }
    }

    # 处理 maobucv.com 的 HTTPS 请求
    server {
        listen 443 ssl http2;
        server_name maobucv.com;

        ssl_certificate /etc/nginx/ssl/maobucv.crt;
        ssl_certificate_key /etc/nginx/ssl/maobucv.pem;

        # 让 WordPress 通过子目录访问
        location ^~ /articles/ {
	       client_max_body_size 50M;
	       rewrite ^/articles(/.*)$ $1 break;
           proxy_set_header Host $host;
           proxy_set_header X-Forwarded-Host $host:$server_port;
           proxy_set_header X-Real-IP $remote_addr;
           proxy_set_header X-Forwarded-For $proxy_add_x_forwarded_for;
           proxy_set_header X-Forwarded-Proto $scheme;
           proxy_pass http://134.175.233.176:3210/;
        }

	    # 重定向页面
        location ~ /wp-(admin|login|content|includes)/ {
           client_max_body_size 50M;
           return 301 /articles$request_uri;
        }

        location / {
            root /usr/share/nginx/html;
            index index.html index.htm;
            try_files $uri $uri/ /index.html;
        }

        # 转发 /huajian 接口到指定地址
        location /huajian {
            proxy_pass http://134.175.233.176:3399;
        }

        # 处理 Vue 路由刷新
        location @router {
            rewrite ^.*$ /index.html last;
        }
    }
}

{
  "compilerOptions": {
    "typeRoots": [
      "node_modules/@types", // 默认值
      "src/interface"
    ],
    "target": "esnext",
    "useDefineForClassFields": true,
    "module": "esnext",
