{
  "name": "vue-data-board",
  "version": "0.0.0",
  "private": true,
  "description": "A Data Analysis Board. A magical DIY databoard tool built with Vue.js. An out-of-box data solution for enterprise data analysis. Support self defined databoard and data report.",
  "author": "DongSuo <xuxiaofei1.0@outlook.com>",
  "scripts": {
    "serve": "vue-cli-service serve",
    "build": "vue-cli-service build --modern",
    "lint": "vue-cli-service lint",
    "analyze": "vue-cli-service build --preview --report"
  },
  "dependencies": {
    "@sentry/browser": "^5.15.5",
    "@sentry/integrations": "^5.15.5",
    "axios": "0.18.1",
    "core-js": "^3.6.4",
    "driver.js": "^0.9.6",
    "echarts": "^5.2.2",
    "element-ui": "^2.13.0",
    "file-saver": "^2.0.1",
    "js-cookie": "^2.2.0",
    "vue": "^2.6.11",
    "vue-grid-layout": "^2.3.12",
    "vue-i18n": "^8.17.5",
    "vue-router": "^3.1.6",
    "vuedraggable": "^2.21.0",
    "vuex": "^3.1.1",
    "xlsx": "^0.17.4"
  },
  "devDependencies": {
    "@babel/eslint-parser": "^7.16.5",
    "@vue/cli-plugin-babel": "~4.5.9",
    "@vue/cli-plugin-eslint": "~4.5.9",
    "@vue/cli-service": "~4.5.9",
    "eslint": "^6.8.0",
    "eslint-plugin-vue": "^6.2.1",
    "node-sass": "^4.13.1",
    "sass-loader": "^8.0.2",
    "script-loader": "^0.7.2",
    "svg-sprite-loader": "^4.1.6",
    "vue-template-compiler": "^2.6.11"
  },
