const fs = require('fs');

fs.readFile('package.json', 'utf8', (err, data) => {
    if (err) {
        console.error('Error reading package.json:', err);
        return;
    }
    let packageJson = JSON.parse(data);

    packageJson.devDependencies = {
        ...packageJson.devDependencies,
        "vue-data-ui": "file:../vue-data-ui"
    };

    fs.writeFile('package.json', JSON.stringify(packageJson, null, 2), 'utf8', (err) => {
        if (err) {
            console.error('Error writing to package.json:', err);
            return;
        }
        console.log('-- DEV MODE : Local vue-data-ui package added successfully --');
    });
});

const fs = require("fs");
const path = require("path");

const currentDir = path.dirname(require.main.filename);

function deleteFolderRecursive(path) {
    if (fs.existsSync(path) && fs.lstatSync(path).isDirectory()) {
        fs.readdirSync(path).forEach(function (file, index) {
            let curPath = path + "/" + file;

            if (fs.lstatSync(curPath).isDirectory()) { // recurse
                deleteFolderRecursive(curPath);
            } else { // delete file
                fs.unlinkSync(curPath);
            }
        });

        console.log(`Deleting directory "${path}"...`);
        fs.rmdirSync(path);
    }
}

deleteFolderRecursive(`${currentDir}\\node_modules\\vue-data-ui`);
deleteFolderRecursive(`${currentDir}\\node_modules\\.vite`);
deleteFolderRecursive('dist');

const fs = require("fs");
const path = require("path");

// Get the directory path of the current module
const currentDir = path.dirname(require.main.filename);

// Resolve the paths to the types and dist directories
const typesDir = path.resolve(currentDir, "documentation");
const distDir = path.resolve(currentDir, "dist");

// Create dist directory if it doesn't exist
if (!fs.existsSync(distDir)) {
  fs.mkdirSync(distDir);
}

// Resolve the path to the dist/types directory
const distTypesDir = path.join(distDir, "documentation");

// Create dist/types directory if it doesn't exist
if (!fs.existsSync(distTypesDir)) {
  fs.mkdirSync(distTypesDir);
}

// Copy .d.ts files from types directory to dist/types directory
fs.readdirSync(typesDir).forEach((file) => {
  const srcFile = path.join(typesDir, file);
  const distFile = path.join(distTypesDir, file);

  fs.copyFileSync(srcFile, distFile);
});

console.log("Doc directory copied successfully.");

const fs = require("fs");
const path = require("path");

// Get the directory path of the current module
const currentDir = path.dirname(require.main.filename);

// Resolve the paths to the types and dist directories
const typesDir = path.resolve(currentDir, "types");
const distDir = path.resolve(currentDir, "dist");

// Create dist directory if it doesn't exist
if (!fs.existsSync(distDir)) {
  fs.mkdirSync(distDir);
}

// Resolve the path to the dist/types directory
const distTypesDir = path.join(distDir, "types");

// Create dist/types directory if it doesn't exist
if (!fs.existsSync(distTypesDir)) {
  fs.mkdirSync(distTypesDir);
}

// Copy .d.ts files from types directory to dist/types directory
fs.readdirSync(typesDir).forEach((file) => {
  const srcFile = path.join(typesDir, file);
  const distFile = path.join(distTypesDir, file);

  fs.copyFileSync(srcFile, distFile);
  fs.copyFileSync(srcFile, distFile.replace(/\.d\.ts$/, ".d.cts"));
});

console.log("Types copied successfully.");

import { defineConfig } from "cypress";

import fs from "fs";

export default defineConfig({
  component: {
    devServer: {
      framework: "vue",
      bundler: "vite",
    },
    setupNodeEvents(on, config) {
      on('task', {
        clearDownloads() {
          const downloadsFolder = 'cypress/downloads';

          try {
            fs.readdirSync(downloadsFolder).forEach((file) => {
              const filePath = `${downloadsFolder}/${file}`;
              fs.unlinkSync(filePath);
            });
            console.log('Downloads folder cleared.');
            return null;
          } catch (err) {
            console.error(`Error clearing downloads folder: ${err}`);
            return err;
          }
        },
      });
    }
  },
});

const fs = require('fs');

function removeKey(obj, key) {
    const { [key]: omit, ...rest } = obj;
    return rest;
}
fs.readFile('package.json', 'utf8', (err, data) => {
    if (err) {
        console.error('Error reading package.json:', err);
        return;
    }

    let packageJson = JSON.parse(data);

    const keyToRemove = "vue-data-ui";

    if (packageJson.devDependencies && packageJson.devDependencies[keyToRemove]) {
        packageJson.devDependencies = removeKey(packageJson.devDependencies, keyToRemove);

        fs.writeFile('package.json', JSON.stringify(packageJson, null, 2), 'utf8', (err) => {
            if (err) {
                console.error('Error writing to package.json:', err);
                return;
            }
            console.log('-- BUILD : Removed local vue-data-ui dev dependency before build --');
        });
    } else {
        console.log(`Key '${keyToRemove}' not found in devDependencies.`);
    }
});

