    defineProps: 'readonly',
  },
  overrides: [
    {
      files: ['*.md'],
      processor: 'markdown/markdown',
      rules: {
        'no-console': 'off',
      },
    },
    {
      files: ['*.ts', '*.tsx'],
      // extends: ['@vue/typescript/recommended', '@vue/prettier'],
