import dotenv from 'dotenv';
import express from 'express';
import path from 'path';
import { fileURLToPath } from 'url';
import { createProxyMiddleware } from 'http-proxy-middleware';

dotenv.config();

const frontendApp = express();
const backEndPort = parseInt(process.env.BACKEND_PORT || 11966, 10);
const frontEndPort = parseInt(process.env.FRONTEND_PORT || 18966, 10);

const __filename = fileURLToPath(import.meta.url);
const __dirname = path.dirname(__filename);

// API ËØ∑Ê±Ç‰ª£ÁêÜÂà∞ÂêéÁ´ØÊúçÂä°
frontendApp.use('/api', createProxyMiddleware({ 
  target: `http://localhost:${backEndPort}/api`,
  changeOrigin: true
}));

// ËÆæÁΩÆÈùôÊÄÅÊñá‰ª∂ÁõÆÂΩï
frontendApp.use(express.static(path.join(__dirname, './dist')));

// ÂêØÂä®ÈùôÊÄÅÊñá‰ª∂ÊúçÂä°
frontendApp.listen(frontEndPort, () => {
  console.log(`Static file server running on port http://localhost:${frontEndPort}`);
});

import dotenv, { parse } from 'dotenv';
import { defineConfig } from 'vite'
import vue from '@vitejs/plugin-vue'
import { VitePWA } from 'vite-plugin-pwa'
import { CodeInspectorPlugin } from 'code-inspector-plugin';

dotenv.config();

const backEndPort = parseInt(process.env.BACKEND_PORT || 11966, 10);
const frontEndPort = parseInt(process.env.FRONTEND_PORT || 18966, 10);

export default defineConfig({
  plugins: [
    vue({
      template: {
        compilerOptions: {
          isCustomElement: (tag) => tag === 'pwa-install'
        }
      }
    }),
    VitePWA({
      registerType: 'autoUpdate',
      injectRegister: 'auto',
      workbox: {
        globPatterns: [
          '**/*.{js,css,html,woff,woff2}',
          '*.{js,css,html,png,svg,jpg,webp}',
        ],
        runtimeCaching: [
          {
            urlPattern: /\/(sw\.js|registerSW\.js|manifest\.webmanifest)$/, // sw Êñá‰ª∂
            handler: 'NetworkFirst',
            options: {
              cacheName: 'critical-assets',
              expiration: {
                maxEntries: 3,
                maxAgeSeconds: 4 * 60 * 60, // 4 Â∞èÊó∂
              },
            },
          },
          {
            urlPattern: /\.(?:png|jpg|jpeg|svg|webp|woff|woff2)$/, // ÂõæÁâáÊñá‰ª∂
            handler: 'CacheFirst',
            options: {
              cacheName: 'images',
              expiration: {
                maxEntries: 60,
                maxAgeSeconds: 7 * 24 * 60 * 60, // 7 Â§©
              },
            },
          },
          {
            urlPattern: /\.(?:js|css)$/, // JS Âíå CSS Êñá‰ª∂
            handler: 'StaleWhileRevalidate',
            options: {
              cacheName: 'assets',
              expiration: {
                maxEntries: 30,
                maxAgeSeconds: 3 * 24 * 60 * 60, // 3 Â§©
              },
            },
          },
          {
            urlPattern: /\/$/, // HTML
            handler: 'NetworkFirst',
            options: {
              cacheName: 'html',
              expiration: {
                maxEntries: 10,
                maxAgeSeconds: 3 * 24 * 60 * 60, // 3 Â§©
              },
            },
          }
        ]
      },
      manifest: {
        name: 'IPCheck.ing',
        short_name: 'IPCheck.ing',
        theme_color: '#f8f9fa',
        orientation: "portrait",
        id: 'com.jasonng.myip',
        description: 'All in one IP Toolbox',
        icons: [
          {
            src: '/logos/logo-192.webp',
            sizes: '192x192',
            type: 'image/webp',
            purpose: 'maskable'
          },
          {
            src: '/logos/ios-logo-192.png',
            sizes: '192x192',
            type: 'image/png',
          },
          {
            src: '/logos/ios-logo-512.png',
            sizes: '512x512',
            type: 'image/png',
          },
        ],
      },
    }),    
    CodeInspectorPlugin({
      bundler: 'vite',
      hideDomPathAttr: true,
      behavior: {
        copy: '{file}',
      },
    }),
  ],
  resolve: {
    alias: {
      '@': '/frontend',
    },
  },
  build: {
    rollupOptions: {
      output: {
        manualChunks(id) {
          if (id.includes('node_modules')) {
            return 'vendor';
          }
        },
        assetFileNames: (assetInfo) => {
          if (assetInfo.name.endsWith('.woff') || assetInfo.name.endsWith('.woff2')) {
            return 'fonts/[name][extname]';
          }
          return 'assets/[name]-[hash][extname]';
        },
      }
    },
    chunkSizeWarningLimit: 1000,
  },
  server: {
    host: '0.0.0.0',
    port: frontEndPort,
    proxy: {
      '/api': `http://localhost:${backEndPort}`
    }
  }
})
