{
    "compilerOptions": {
        "allowJs": true,
        "strict": true,
        "module": "ES2020",
        "moduleResolution": "node",
        "target": "ES2020",
        "esModuleInterop": true,
        "isolatedModules": true,
        "resolveJsonModule": true,
        "baseUrl": ".",
        "paths": {
            "@main/*": ["src/*"],
            "@foo/*": ["tests/*"]
        },
        "declaration": true,
        "declarationDir": "./@types",
        "emitDeclarationOnly": true
    },
    "include": [
        "src"
    ],
    "exclude": [
        "node_modules"
    ]
}
