{
  "name": "bmack/composer-projectcontext",
  "type": "composer-plugin",
  "license": "MIT",
  "description": "Versatile lightweight package to detect a composers' root path.",
  "keywords": [
    "project root", "composer"
  ],
  "homepage": "http://www.b13.de/",
  "authors": [
    {
      "name": "Benni Mack",
      "email": "benjamin.mack@gmail.com"
    }
  ],
  "minimum-stability": "dev",
  "autoload": {
    "psr-4": { "Bmack\\ComposerProjectContext\\": "src/"}
  },
  "require": {
    "php": ">=5.5.0",
    "composer-plugin-api": "^1.0.0"
  },
  "require-dev": {
    "composer/composer": "1.*"
  },
  "extra": {
    "class": "Bmack\\ComposerProjectContext\\Composer\\Plugin",
    "branch-alias": {
      "dev-master": "1.0.x-dev"
    }
  }
}
