{
  "env": {
    "es6": true,
    "browser": true,
    "mocha": true,
    "node": true,
    "jest": true
  },
  "parser": "@babel/eslint-parser",
  "parserOptions": {
    "babelOptions": {
      "presets": ["@babel/preset-react"]
    }
  },
  "root": true,
  "extends": [
    "eslint:recommended",
    "plugin:react/recommended",
    "plugin:react-hooks/recommended",
    "plugin:import/recommended",
    "plugin:jsx-a11y/recommended",
    "prettier"
  ],
  "plugins": ["no-only-tests", "filenames-simple"],
  "globals": {
    "Promise": true,
    "window": true,
    "$": true,
    "ga": true,
    "jQuery": true,
    "router": true
  },
  "settings": {
    "react": {
      "version": "16.4.2"
    },
    "import/resolver": {
      "typescript": true,
      "node": true
    }
  },
  "rules": {
    "import/no-unresolved": [2, { "commonjs": true }],
    "import/named": "error",
    "import/no-named-as-default": "off",
    "import/no-named-as-default-member": "off",
    "import/order": "error",
    "import/no-cycle": [2, { "maxDepth": 2 }],
    "react/prop-types": "off",
    "no-only-tests/no-only-tests": "error",
    "no-unused-vars": "off",
    "@typescript-eslint/no-unused-vars": [
      "warn",
      {
        "argsIgnorePattern": "^_",
        "varsIgnorePattern": "^_",
        "caughtErrorsIgnorePattern": "^_"
      }
    ],
    "filenames-simple/naming-convention": ["warn"]
  },
  "overrides": [
    {
      "files": ["**/*.ts?(x)"],
      "parser": "@typescript-eslint/parser",
      "parserOptions": {
        "project": [
          "./client/tsconfig.json",
          "./tsconfig.json",
          "./api/tsconfig.json",
          "./shared/tsconfig.json",
          "./tools/client-plugins/browser-scripts/tsconfig.json",
          "./e2e/tsconfig.json"
        ]
      },
      "extends": [
        "plugin:@typescript-eslint/recommended",
        "plugin:@typescript-eslint/recommended-requiring-type-checking",
        "plugin:import/typescript"
      ],
      "plugins": ["@typescript-eslint"],
      "rules": {
        "import/no-unresolved": "off",
        "import/named": 0,
        "@typescript-eslint/naming-convention": "off"
      }
    },
    {
      "files": ["./client/**/*.test.[jt]s?(x)"],
      "extends": [
        "plugin:testing-library/react",
        "plugin:jest-dom/recommended"
      ],
      "rules": { "import/named": 2 }
    },
    {
      "files": ["e2e/*.ts"],
      "rules": {
        "@typescript-eslint/no-unsafe-member-access": "off",
        "@typescript-eslint/no-unsafe-call": "off",
        "@typescript-eslint/no-unsafe-assignment": "off"
      }
    },
    {
      "files": ["**/api-server/**/*", "**/404.*"],
      "rules": {
        "filenames-simple/naming-convention": "off"
      }
    },
    {
      "files": ["**/api/src/**/*.ts"],
      "plugins": ["jsdoc"],
      "extends": ["plugin:jsdoc/recommended-typescript-error"],
      "rules": {
        "jsdoc/require-jsdoc": [
          "error",
          {
            "require": {
              "ArrowFunctionExpression": true,
              "ClassDeclaration": true,
              "ClassExpression": true,
              "FunctionDeclaration": true,
              "FunctionExpression": true,
              "MethodDefinition": true
            },
            "publicOnly": true
          }
        ],
        "jsdoc/require-description-complete-sentence": "warn",
        "jsdoc/tag-lines": "off"
      }
    }
  ]
}

const { ESLint } = require('eslint');

const cli = new ESLint();

// This lets us abort if we've already run a stage for all files
const completedStages = new Set();

// if a lot of files are changed, it's faster to run prettier/eslint on the
// whole project than to run them on each file separately
module.exports = {
  '*.(js|ts|tsx)': async files => {
    if (completedStages.has('js')) return [];

    const ignoredIds = await Promise.all(
      files.map(file => cli.isPathIgnored(file))
    );
    const lintableFiles = files.filter((_, i) => !ignoredIds[i]);
    if (files.length > 10) {
      completedStages.add('js');
      return ['eslint --max-warnings=0 --cache --fix .', 'prettier --write .'];
    } else {
      return [
        'eslint --max-warnings=0 --cache --fix ' + lintableFiles.join(' '),
        ...files.map(filename => `prettier --write '${filename}'`)
      ];
    }
  },
  '*.!(js|ts|tsx)': files => {
    if (completedStages.has('not-js')) return [];

    if (files.length > 10) {
      completedStages.add('not-js');
      return 'prettier --write .';
    } else {
      return files.map(
        filename => `prettier --write --ignore-unknown '${filename}'`
      );
    }
  },

  './curriculum/challenges/**/*.md': files => {
    if (completedStages.has('markdown')) return [];

    if (files.length > 10) {
      completedStages.add('markdown');
      return 'pnpm run lint:challenges';
    } else {
      return files.map(
        filename => `node ./tools/scripts/lint/index.js '${filename}'`
      );
    }
  }
};

module.exports = {
  babelrcRoots: ['./api-server', './client']
};

module.exports = {
  testPathIgnorePatterns: ['/node_modules/', 'api/', 'e2e/'],
  moduleNameMapper: {
    '\\.(jpg|jpeg|png|svg|woff|woff2)$':
      '<rootDir>/client/src/__mocks__/file-mock.ts',
    // Plain CSS - match css files that don't end with
    // '.module.css' https://regex101.com/r/VzwrKH/4
    '^(?!.*\\.module\\.css$).*\\.css$':
      '<rootDir>/client/src/__mocks__/style-mock.ts',
    // CSS Modules - match files that end with 'module.css'
    '\\.module\\.css$': 'identity-obj-proxy',
    '^lodash-es$': 'lodash'
  },
  globals: {
    __PATH_PREFIX__: ''
  },
  verbose: true,
  transform: {
    '^.+\\.[jt]sx?$': '<rootDir>/jest.transform.js'
  },
  roots: ['.', './client', './api-server'],
  transformIgnorePatterns: ['node_modules/.pnpm/(?!(nanoid|uuid)@)'],
  setupFilesAfterEnv: ['./jest.setup.js'],
  testEnvironment: 'jsdom',
  watchPathIgnorePatterns: ['<rootDir>/__fixtures__.*']
};

const babelOptions = {
  presets: [
    [
      '@babel/preset-env',
      {
        targets: {
          node: 18
        }
      }
    ],
    '@babel/react',
    '@babel/preset-typescript'
  ]
};

// TODO: is there a way to do this without a separate transform? i.e. can we
// just use the existing config?
module.exports = require('babel-jest').default.createTransformer(babelOptions);

{
  "$schema": "https://unpkg.com/knip@5/schema.json",
  "ignoreBinaries": ["create:shared", "install-puppeteer", "pm2"],
  "ignoreWorkspaces": ["api-server"], // Ignored based on https://github.com/freeCodeCamp/freeCodeCamp/pull/52330#issuecomment-1807917235
  "workspaces": {
    ".": {
      "playwright": [
        "playwright.config.ts",
        "playwright-mobile.config.ts" // How/where is this file used?
      ],
      "ignore": ["tools/scripts/redirect-gen.ts"] // Referenced in tools/scripts/redirect-gen.ts
    },
    "api": {
      "ignoreDependencies": ["pino-pretty"] // Knip doesn't have a fastify plugin yet
    },
    "client": {
      "entry": ["static/**/*.js"],
      "webpack": "webpack-workers.js",
      "ignore": [
        "**/__mocks__/**",
        "tailwind.config.js",
        "src/assets/icons/*.tsx" // Ignored based on https://github.com/freeCodeCamp/freeCodeCamp/pull/56244#issuecomment-2367830791
      ],
      "ignoreDependencies": [
        "core-js",
        // Node.js built-ins need to be ignored
        "process",
        "util",
        "assert",
        "@redux-saga/core" // Not referenced, something seems off related to peer dep and types
      ]
    },
    "curriculum": {
      "ignoreDependencies": ["invariant"] // Used from another workspace: shared/utils/polyvinyl.js
    },
    "shared": {
      "ignore": ["{config,utils}/*.ts"] // Using the same dir for src + outDir files with tsc confuses Knip
    },
    "tools/challenge-helper-scripts": {
      "ignoreBinaries": ["mocha"] // Test setup in this workspace seems to be in need of a checkup
    },
    "tools/challenge-parser": {
      "entry": ["parser/tools/*.js"] // Files not referenced/documented?
    },
    "tools/client-plugins/browser-scripts": {
      "ignoreDependencies": ["sass.js", "xterm", "process", "util"] // Node.js built-ins need to be ignored
    },
    "tools/scripts/build": {
      "entry": ["*.ts"]
    },
    "tools/scripts/seed-exams": {
      "entry": ["add-nano-ids.js"] // Referenced in tools/scripts/seed-exams/README.md
    }
  }
}

