{
  "$schema": "https://developer.microsoft.com/json-schemas/api-extractor/v7/api-extractor.schema.json",

  "projectFolder": ".",

  "compiler": {
    "tsconfigFilePath": "api-extractor.tsconfig.json"
  },

  "mainEntryPointFilePath": "./temp/src/v3/index.d.ts",

  "dtsRollup": {
    "enabled": true,
    "untrimmedFilePath": "",
    "publicTrimmedFilePath": "./types/v3-generated.d.ts"
  },

  "apiReport": {
    "enabled": false
  },

  "docModel": {
    "enabled": false
  },

  "tsdocMetadata": {
    "enabled": false
  },

  "messages": {
    "compilerMessageReporting": {
      "default": {
        "logLevel": "warning"
      }
    },

    "extractorMessageReporting": {
      "default": {
        "logLevel": "warning",
        "addToApiReportFile": true
      },

      "ae-missing-release-tag": {
        "logLevel": "none"
      },
      "ae-internal-missing-underscore": {
        "logLevel": "none"
      },
      "ae-forgotten-export": {
        "logLevel": "none"
      }
    },

    "tsdocMessageReporting": {
      "default": {
        "logLevel": "warning"
      },

      "tsdoc-undefined-tag": {
        "logLevel": "none"
      }
    }
  }
}

{
  "extends": "./tsconfig.json",
  "compilerOptions": {
    "baseUrl": "./temp",
    "types": []
  }
}

import { resolve as _resolve } from 'path'
import { defineConfig } from 'vitest/config'

const resolve = (p: string) => _resolve(__dirname, p)

export default defineConfig({
  resolve: {
    alias: {
      compiler: resolve('src/compiler'),
      core: resolve('src/core'),
      server: resolve('packages/server-renderer/src'),
      sfc: resolve('packages/compiler-sfc/src'),
      shared: resolve('src/shared'),
      web: resolve('src/platforms/web'),
      v3: resolve('src/v3'),
      vue: resolve('src/platforms/web/entry-runtime-with-compiler'),
      types: resolve('src/types')
    }
  },
  define: {
    __DEV__: true,
    __TEST__: true
  },
  test: {
    globals: true,
    environment: 'jsdom',
    setupFiles: resolve('test/vitest.setup.ts')
  }
})

{
  "presets": ["@babel/preset-env"]
}

{
  "root": true,
  "extends": "airbnb",
  "plugins": ["jest"],
  "env": {
    "jest/globals": true
  },
  "rules": {
    "no-bitwise": "off",
    "no-lonely-if": "off",
    "class-methods-use-this": "off",
    "arrow-body-style": "off",
    "no-loop-func": "off"
  },
  "ignorePatterns": ["*.md", "*.png", "*.jpeg", "*.jpg"],
  "settings": {
    "react": {
      "version": "18.2.0"
    }
  }
}

module.exports = {
  // The bail config option can be used here to have Jest stop running tests after
  // the first failure.
  bail: false,

  // Indicates whether each individual test should be reported during the run.
  verbose: false,

  // Indicates whether the coverage information should be collected while executing the test
  collectCoverage: false,

  // The directory where Jest should output its coverage files.
  coverageDirectory: './coverage/',

  // If the test path matches any of the patterns, it will be skipped.
  testPathIgnorePatterns: ['<rootDir>/node_modules/'],

  // If the file path matches any of the patterns, coverage information will be skipped.
  coveragePathIgnorePatterns: ['<rootDir>/node_modules/'],

  // The pattern Jest uses to detect test files.
  testRegex: '(/__tests__/.*|(\\.|/)(test|spec))\\.jsx?$',

  // This option sets the URL for the jsdom environment.
  // It is reflected in properties such as location.href.
  // @see: https://github.com/facebook/jest/issues/6769
  testEnvironmentOptions: {
    url: 'http://localhost/',
  },

  // @see: https://jestjs.io/docs/en/configuration#coveragethreshold-object
  coverageThreshold: {
    global: {
      statements: 100,
      branches: 95,
      functions: 100,
      lines: 100,
    },
  },
};

