load("@bazel_skylib//lib:paths.bzl", "paths")
load(":build_variables.bzl", "aten_ufunc_headers")

aten_ufunc_names = [
    paths.split_extension(paths.basename(h))[0]
    for h in aten_ufunc_headers
]

def aten_ufunc_generated_cpu_sources(gencode_pattern = "{}"):
    return [gencode_pattern.format(name) for name in [
        "UfuncCPU_{}.cpp".format(n)
        for n in aten_ufunc_names
    ]]

def aten_ufunc_generated_cpu_kernel_sources(gencode_pattern = "{}"):
    return [gencode_pattern.format(name) for name in [
        "UfuncCPUKernel_{}.cpp".format(n)
        for n in aten_ufunc_names
    ]]

def aten_ufunc_generated_cuda_sources(gencode_pattern = "{}"):
    return [gencode_pattern.format(name) for name in [
        "UfuncCUDA_{}.cu".format(n)
        for n in aten_ufunc_names
    ]]

-- Configuration file for LuaCov

local source = require("lfs").currentdir()

local function pesc(s)
  assert(type(s) == 'string', s)
  return s:gsub('[%(%)%.%%%+%-%*%?%[%]%^%$]', '%%%1')
end

return {
  include = {
    -- Absolute paths (starting with source dir, not hidden (i.e. .deps)).
    pesc(source) .. "[/\\][^.].+",
    -- Relative (non-hidden) paths.
    '^[^/\\.]',
  },
  modules = {
    ['vim'] = 'runtime/lua/vim/shared.lua'
  },
}

-- vim: ft=lua tw=80 sw=2 et

{
  "$schema": "https://raw.githubusercontent.com/LuaLS/vscode-lua/master/setting/schema.json",
  "runtime": {
    "version": "LuaJIT"
  },
  "workspace": {
    "library": [
      "runtime/lua",
      "${3rd}/busted/library",
      "${3rd}/luv/library"
    ],
    "ignoreDir": [
      "test"
    ],
    "checkThirdParty": "Disable"
  },
  "diagnostics": {
    "groupFileStatus": {
      "strict": "Opened",
      "strong": "Opened"
    },
    "groupSeverity": {
      "strong": "Warning",
      "strict": "Warning"
    },
    "unusedLocalExclude": [ "_*" ],
    "disable": [
      "luadoc-miss-see-name"
    ]
  }
}

.DONE:
	@echo "Use GNU Make (gmake) to build neovim"
.DEFAULT:
	@echo "Use GNU Make (gmake) to build neovim"

{
  "version": 3,
  "configurePresets": [
    {
      "name": "base",
      "generator": "Ninja",
      "binaryDir": "${sourceDir}/build",
      "hidden": true
    },
    {
      "name": "default",
      "displayName": "RelWithDebInfo",
      "description": "Enables optimizations (-O2) with debug information",
      "cacheVariables": {
        "CMAKE_BUILD_TYPE": "RelWithDebInfo"
      },
      "inherits": ["base"]
    },
    {
      "name": "debug",
      "displayName": "Debug",
      "description": "Disables optimizations (-O0), enables debug information",
      "cacheVariables": {
        "CMAKE_BUILD_TYPE": "Debug"
      },
      "inherits": ["base"]
    },
    {
      "name": "release",
      "displayName": "Release",
      "description": "Same as RelWithDebInfo, but disables debug information",
      "cacheVariables": {
        "CMAKE_BUILD_TYPE": "Release"
      },
      "inherits": ["base"]
    },
    {
      "name": "iwyu",
      "displayName": "IWYU",
      "description": "Run include-what-you-use",
      "cacheVariables": {
        "ENABLE_IWYU": "ON"
      },
      "inherits": ["base"]
    },
    {
      "name": "ci",
      "cacheVariables": {
        "CI_BUILD": "ON"
      },
      "inherits": ["base"]
    }
  ]
}

{
  "name": "@tailwindcss/root",
  "private": true,
  "version": "1.0.0",
  "prettier": {
    "semi": false,
    "singleQuote": true,
    "printWidth": 100,
    "plugins": [
      "prettier-plugin-organize-imports"
    ],
    "overrides": [
      {
        "files": [
          "tsconfig.json"
        ],
        "options": {
          "parser": "jsonc"
        }
      },
      {
        "files": [
          "integrations/**/*.ts"
        ],
        "options": {
          "plugins": [
            "prettier-plugin-embed",
            "prettier-plugin-organize-imports"
          ]
        }
      }
    ]
  },
  "scripts": {
    "format": "prettier --write .",
    "lint": "prettier --check . && turbo lint",
    "build": "turbo build --filter=!./playgrounds/*",
    "postbuild": "node ./scripts/pack-packages.mjs",
    "dev": "turbo dev --filter=!./playgrounds/*",
    "test": "cargo test && vitest run --hideSkippedTests",
    "test:integrations": "vitest --root=./integrations",
    "test:ui": "pnpm run --filter=tailwindcss test:ui && pnpm run --filter=@tailwindcss/browser test:ui",
    "tdd": "vitest --hideSkippedTests",
    "bench": "vitest bench",
    "version-packages": "node ./scripts/version-packages.mjs",
    "vite": "pnpm run --filter=vite-playground dev",
    "nextjs": "pnpm run --filter=nextjs-playground dev"
  },
  "license": "MIT",
  "devDependencies": {
    "@playwright/test": "^1.49.1",
    "@types/node": "catalog:",
    "postcss": "8.4.49",
    "postcss-import": "^16.1.0",
    "prettier": "^3.4.2",
    "prettier-plugin-embed": "^0.4.15",
    "prettier-plugin-organize-imports": "^4.0.0",
    "tsup": "^8.2.4",
    "turbo": "^2.3.3",
    "typescript": "^5.5.4",
    "vitest": "^2.0.5"
  },
  "packageManager": "pnpm@9.6.0",
  "pnpm": {
    "patchedDependencies": {
      "lightningcss@1.29.1": "patches/lightningcss@1.29.1.patch",
      "@parcel/watcher@2.5.0": "patches/@parcel__watcher@2.5.0.patch"
    }
  }
}

{
  "$schema": "https://turbo.build/schema.json",
  "ui": "tui",
  "tasks": {
    "@tailwindcss/oxide#build": {
      "dependsOn": ["^build"],
      "outputs": ["./index.d.ts", "./index.js", "./*.node"],
      "inputs": [
        "./src/**/*",
        "./build.rs",
        "./package.json",
        "./Cargo.toml",
        "../oxide/src/**/*",
        "../oxide/Cargo.toml",
        "../Cargo.toml",
        "../package.json"
      ]
    },
    "@tailwindcss/oxide#dev": {
      "dependsOn": ["^dev"],
      "outputs": ["./index.d.ts", "./index.js", "./*.node"],
      "inputs": [
        "./src/**/*",
        "./build.rs",
        "./package.json",
        "./Cargo.toml",
        "../oxide/src/**/*",
        "../oxide/Cargo.toml",
        "../Cargo.toml",
        "../package.json"
      ],
      "cache": false,
      "persistent": true
    },
    "build": {
      "dependsOn": ["^build"],
      "outputs": ["dist/**"]
    },
    "lint": {},
    "dev": {
      "cache": false,
      "persistent": true
    }
  },
  // If rustup is installed outside of the default ~/.rustup directory, we need
  // to pass the path through to the individual rust tasks.
  "globalPassThroughEnv": ["RUSTUP_HOME"]
}

module.exports = {
	contents: ['docs/_sidebar.md'], // array of "table of contents" files path
	pathToPublic: 'pdf/readme.pdf', // path where pdf will stored
	pdfOptions: {
		margin: { top: '100px', bottom: '100px' }
	}, // reference: https://github.com/GoogleChrome/puppeteer/blob/master/docs/api.md#pagepdfoptions
	removeTemp: true, // remove generated .md and .html or not
	emulateMedia: 'print', // mediaType, emulating by puppeteer for rendering pdf, 'print' by default (reference: https://github.com/GoogleChrome/puppeteer/blob/master/docs/api.md#pageemulatemediamediatype)
};

