    {
        files: ["**/*.mjs"],
        languageOptions: {
            sourceType: "module"
        }
    },
    {
        ignores: [
            "**/*.min.js",
            "**/vendor/**/*.js",
            "django/contrib/gis/templates/**/*.js",
            "django/views/templates/*.js",
            "docs/_build/**/*.js",
            "node_modules/**.js",
            "tests/**/*.js",
        ]
    }
];

