{
  "$schema": "https://docs.renovatebot.com/renovate-schema.json",
  "extends": [
    "github>elastic/renovate-config:only-chainguard",
    ":disableDependencyDashboard"
  ],
  "schedule": [
    "after 1pm on tuesday"
  ],
  "labels": [">non-issue", ":Delivery/Packaging", "Team:Delivery", "auto-merge-without-approval"],
  "baseBranches": ["main", "8.x", "8.17", "8.16"],
  "packageRules": [
    {
      "groupName": "wolfi (versioned)",
      "groupSlug": "wolfi-versioned",
      "description": "Override the `groupSlug` to create a non-special-character branch name",
      "matchDatasources": [
        "docker"
      ],
      "matchPackagePatterns": [
        "^docker.elastic.co/wolfi/chainguard-base$"
      ]
    }
  ],
  "customManagers": [
    {
      "description": "Extract Wolfi images from elasticsearch DockerBase configuration",
      "customType": "regex",
      "fileMatch": [
        "build\\-tools\\-internal\\/src\\/main\\/java\\/org\\/elasticsearch\\/gradle\\/internal\\/DockerBase\\.java$"
      ],
      "matchStrings": [
        "\\s*\"?(?<depName>[^\\s:@\"]+)(?::(?<currentValue>[-a-zA-Z0-9.]+))?(?:@(?<currentDigest>sha256:[a-zA-Z0-9]+))?\"?"
      ],
      "currentValueTemplate": "{{#if currentValue}}{{{currentValue}}}{{else}}latest{{/if}}",
      "autoReplaceStringTemplate": "{{{depName}}}{{#if newValue}}:{{{newValue}}}{{/if}}{{#if newDigest}}@{{{newDigest}}}{{/if}}\"",
      "datasourceTemplate": "docker"
    }
  ]
}

import org.elasticsearch.gradle.internal.toolchain.OracleOpenJdkToolchainResolver
import org.elasticsearch.gradle.internal.toolchain.ArchivedOracleJdkToolchainResolver
import org.elasticsearch.gradle.internal.toolchain.AdoptiumJdkToolchainResolver

pluginManagement {
  repositories {
    mavenCentral()
    gradlePluginPortal()
  }

  includeBuild "build-conventions"
  includeBuild "build-tools"
  includeBuild "build-tools-internal"
}

plugins {
  id "com.gradle.develocity" version "3.18.1"
  id 'elasticsearch.java-toolchain'
}

enableFeaturePreview "STABLE_CONFIGURATION_CACHE"

rootProject.name = "elasticsearch"

dependencyResolutionManagement {
  versionCatalogs {
    buildLibs {
      from(files("gradle/build.versions.toml"))
    }
  }
}

toolchainManagement {
  jvm {
    javaRepositories {
      repository('bundledOracleOpendJdk') {
        resolverClass = OracleOpenJdkToolchainResolver
      }
      repository('adoptiumJdks') {
        resolverClass = AdoptiumJdkToolchainResolver
      }
      repository('archivedOracleJdks') {
        resolverClass = ArchivedOracleJdkToolchainResolver
      }
    }
  }
}

