{
  "name": "@vitejs/vite-monorepo",
  "private": true,
  "type": "module",
  "engines": {
    "node": "^18.0.0 || ^20.0.0 || >=22.0.0"
  },
  "homepage": "https://vite.dev/",
  "repository": {
    "type": "git",
    "url": "git+https://github.com/vitejs/vite.git"
  },
  "keywords": [
    "frontend",
    "hmr",
    "dev-server",
    "build-tool",
    "vite"
  ],
  "scripts": {
    "preinstall": "npx only-allow pnpm",
    "postinstall": "simple-git-hooks",
    "format": "prettier --write --cache .",
    "lint": "eslint --cache .",
    "typecheck": "tsc -p scripts --noEmit && pnpm -r --parallel run typecheck",
    "test": "pnpm test-unit && pnpm test-serve && pnpm test-build",
    "test-serve": "vitest run -c vitest.config.e2e.ts",
    "test-build": "VITE_TEST_BUILD=1 vitest run -c vitest.config.e2e.ts",
    "test-unit": "vitest run",
    "test-docs": "pnpm run docs-build",
    "debug-serve": "VITE_DEBUG_SERVE=1 vitest run -c vitest.config.e2e.ts",
    "debug-build": "VITE_TEST_BUILD=1 VITE_PRESERVE_BUILD_ARTIFACTS=1 vitest run -c vitest.config.e2e.ts",
    "docs": "pnpm --filter=docs run docs",
    "docs-build": "pnpm --filter=docs run docs-build",
    "docs-serve": "pnpm --filter=docs run docs-serve",
    "build": "pnpm -r --filter='./packages/*' run build",
    "dev": "pnpm -r --parallel --filter='./packages/*' run dev",
    "release": "tsx scripts/release.ts",
    "ci-publish": "tsx scripts/publishCI.ts",
    "ci-docs": "pnpm build && pnpm docs-build"
  },
  "devDependencies": {
    "@eslint/js": "^9.17.0",
    "@type-challenges/utils": "^0.1.1",
    "@types/babel__core": "^7.20.5",
    "@types/babel__preset-env": "^7.9.7",
    "@types/convert-source-map": "^2.0.3",
    "@types/cross-spawn": "^6.0.6",
    "@types/debug": "^4.1.12",
    "@types/estree": "^1.0.6",
    "@types/etag": "^1.8.3",
    "@types/less": "^3.0.7",
    "@types/node": "^22.10.5",
    "@types/picomatch": "^3.0.1",
    "@types/stylus": "^0.48.43",
    "@types/ws": "^8.5.13",
    "@vitejs/release-scripts": "^1.3.2",
    "conventional-changelog-cli": "^5.0.0",
    "eslint": "^9.17.0",
    "eslint-plugin-import-x": "^4.6.1",
    "eslint-plugin-n": "^17.15.1",
    "eslint-plugin-regexp": "^2.7.0",
    "execa": "^9.5.2",
    "globals": "^15.14.0",
    "gsap": "^3.12.5",
    "lint-staged": "^15.3.0",
    "picocolors": "^1.1.1",
    "playwright-chromium": "^1.49.1",
    "premove": "^4.0.0",
    "prettier": "3.4.2",
    "rollup": "^4.23.0",
    "rollup-plugin-esbuild": "^6.1.1",
    "simple-git-hooks": "^2.11.1",
    "tslib": "^2.8.1",
    "tsx": "^4.19.2",
    "typescript": "~5.7.2",
    "typescript-eslint": "^8.19.1",
    "vite": "workspace:*",
    "vitest": "^2.1.8"
  },
  "simple-git-hooks": {
    "pre-commit": "pnpm exec lint-staged --concurrent false"
  },
  "lint-staged": {
    "*": [
      "prettier --write --cache --ignore-unknown"
    ],
    "packages/*/{src,types}/**/*.ts": [
      "eslint --cache --fix"
    ],
    "packages/**/*.d.ts": [
      "eslint --cache --fix"
    ],
    "playground/**/__tests__/**/*.ts": [
      "eslint --cache --fix"
    ]
  },
  "packageManager": "pnpm@9.15.3",
  "pnpm": {
    "overrides": {
      "vite": "workspace:*"
    },
