func TestGetModules(t *testing.T) {
	modulesMu.Lock()
	modules = map[string]ModuleInfo{
		"a":      {ID: "a"},
		"a.b":    {ID: "a.b"},
		"a.b.c":  {ID: "a.b.c"},
		"a.b.cd": {ID: "a.b.cd"},
		"a.c":    {ID: "a.c"},
		"a.d":    {ID: "a.d"},
		"b":      {ID: "b"},
		"b.a":    {ID: "b.a"},
		"b.b":    {ID: "b.b"},
		"b.a.c":  {ID: "b.a.c"},
		"c":      {ID: "c"},
	}
	modulesMu.Unlock()

	for i, tc := range []struct {
		input  string
		expect []ModuleInfo
	}{
		{
			input: "",
			expect: []ModuleInfo{
				{ID: "a"},
				{ID: "b"},
				{ID: "c"},
			},
		},
		{
			input: "a",
			expect: []ModuleInfo{
				{ID: "a.b"},
				{ID: "a.c"},
				{ID: "a.d"},
			},
		},
		{
			input: "a.b",
			expect: []ModuleInfo{
				{ID: "a.b.c"},
				{ID: "a.b.cd"},
			},
		},
		{
			input: "a.b.c",
		},
		{
			input: "b",
			expect: []ModuleInfo{
				{ID: "b.a"},
				{ID: "b.b"},
			},
		},
		{
			input: "asdf",
		},
	} {
		actual := GetModules(tc.input)
		if !reflect.DeepEqual(actual, tc.expect) {
			t.Errorf("Test %d: Expected %v but got %v", i, tc.expect, actual)
		}
	}
}

func TestModuleID(t *testing.T) {
	for i, tc := range []struct {
		input           ModuleID
		expectNamespace string
		expectName      string
	}{
		{
			input:           "foo",
			expectNamespace: "",
			expectName:      "foo",
		},
		{
			input:           "foo.bar",
			expectNamespace: "foo",
			expectName:      "bar",
		},
		{
			input:           "a.b.c",
			expectNamespace: "a.b",
			expectName:      "c",
		},
	} {
		actualNamespace := tc.input.Namespace()
		if actualNamespace != tc.expectNamespace {
			t.Errorf("Test %d: Expected namespace '%s' but got '%s'", i, tc.expectNamespace, actualNamespace)
		}
		actualName := tc.input.Name()
		if actualName != tc.expectName {
			t.Errorf("Test %d: Expected name '%s' but got '%s'", i, tc.expectName, actualName)
		}
	}
}

