{
  "extends": "eslint:recommended",
  "env": {
    "browser": true,
    "commonjs": true,
    "node": true,
    "es6": true,
    "jest": true
  },
  "parserOptions": {
    "ecmaVersion": 2018
  },
  "rules": {
    "no-console": "off",
    "strict": ["error", "global"],
    "curly": "warn"
  },
  "overrides": [
    {
      "files": [
        "docusaurus/website/src/**/*.js",
        "packages/cra-template/**/*.js",
        "packages/react-error-overlay/**/*.js",
        "packages/react-scripts/fixtures/kitchensink/template/{src,integration}/**/*.js",
        "test/fixtures/*/src/*.js"
      ],
      "excludedFiles": ["packages/react-error-overlay/*.js"],
      "extends": ["react-app", "react-app/jest"]
    },
    {
      "files": [
        "test/fixtures/webpack-message-formatting/src/{AppLintError,AppLintWarning,AppUnknownFile}.js"
      ],
      "rules": {
        "no-unused-vars": "off",
        "no-undef": "off"
      }
    },
    {
      "files": ["test/fixtures/webpack-message-formatting/src/Export5.js"],
      "rules": {
        "import/no-anonymous-default-export": "off"
      }
    },
    {
      "files": ["test/fixtures/issue-5176-flow-class-properties/src/App.js"],
      "rules": {
        "no-dupe-class-members": "off"
      }
    }
  ]
}

{
  "arrowParens": "avoid",
  "singleQuote": true,
  "semi": true
}

{
  "lerna": "2.6.0",
  "npmClient": "yarn",
  "useWorkspaces": true,
  "version": "independent",
  "changelog": {
    "repo": "facebook/create-react-app",
    "labels": {
      "tag: new feature": ":rocket: New Feature",
      "tag: breaking change": ":boom: Breaking Change",
      "tag: bug fix": ":bug: Bug Fix",
      "tag: enhancement": ":nail_care: Enhancement",
      "tag: documentation": ":memo: Documentation",
      "tag: internal": ":house: Internal",
      "tag: underlying tools": ":hammer: Underlying Tools"
    },
    "cacheDir": ".changelog"
  }
}
