function getThirdPartyPackages() {
  return fs
    .readdirSync(
      path.join(import.meta.dirname, 'packages/puppeteer-core/third_party'),
      {
        withFileTypes: true,
      },
    )
    .filter(dirent => {
      return dirent.isDirectory();
    })
    .map(({name}) => {
      return {
        name,
        message: `Import \`${name}\` from the vendored location: third_party/${name}/index.js`,
      };
    });
}

