      "matchDepTypes": ["dependencies", "devDependencies"],
      "rangeStrategy": "replace",
      "ignoreDeps": [
        "@babel/core-7.12",
        "@babel/helper-validator-identifier-baseline"
      ],
      "postUpgradeTasks": {
        "commands": ["make"]
