// jsdoc doesn't accept paths starting with _ (which is the case on github runners)
// so we need to alter the default config
{
  "source": {
    "excludePattern": ""
  },
  "templates": {
    "default": {
      "includeDate": false
    }
  },
  "opts": {
    "encoding": "utf8",
    "recurse": true
  },
  "metadata": {
    "title": "Discourse"
  }
}

const fs = require("fs");
const { execSync, execFileSync } = require("child_process");

const discourseRoot = __dirname;

if (fs.existsSync(`${discourseRoot}/node_modules/.yarn-integrity`)) {
  console.log(
    "Detected yarn-managed node_modules. Performing one-time cleanup..."
  );

  // Delete entire contents of all node_modules directories
  // But keep the directories themselves, in case they are volume mounts (e.g. in devcontainer)
  execSync(
    `find ${discourseRoot}/node_modules ${discourseRoot}/app/assets/javascripts/*/node_modules -mindepth 1 -maxdepth 1 -exec rm -rf {} +`
  );

  console.log("cleanup done");
}

const pluginBase = `${discourseRoot}/plugins/`;
const cwd = process.cwd();
const pluginName =
  cwd.startsWith(pluginBase) && cwd.replace(pluginBase, "").split("/", 2)[0];

if (
  pluginName &&
  fs.existsSync(`${discourseRoot}/plugins/${pluginName}/package.json`) &&
  !process.argv.includes("--ignore-workspace")
) {
  console.log(
    "> pnpm was run inside a plugin directory. Re-executing with --ignore-workspace..."
  );

  const indexOfPnpm = process.argv.findIndex(
    (a) => a.includes("/pnpm") || a.endsWith("pnpm")
  );
  const newArgs = [...process.argv];
  newArgs.splice(indexOfPnpm + 1, 0, "--ignore-workspace");

  try {
    execFileSync(newArgs[0], newArgs.slice(1), {
      stdio: "inherit",
    });
  } catch (e) {
    if (e.status) {
      process.exit(e.status);
    }
    throw e;
  }

  process.exit(0);
}

const templateLint = require("@discourse/lint-configs/template-lint");

module.exports = {
  ...templateLint,
  rules: {
    ...templateLint.rules,
    "no-capital-arguments": false, // TODO: we extensively use `args` argument name
    "require-button-type": false,
  },
};

{
  "folders":
  [
    {
      "path": "app",
      "folder_exclude_patterns": ["external", "external_development", "external_production", "images", "imported", "fonts", "defer"],
      "file_exclude_patterns": ["i18n.js"]
    },
    {
      "path": "config",
      "file_exclude_patterns": ["*.ar.yml", "*.bg.yml", "*.bs_BA.yml", "*.ca.yml", "*.cs.yml", "*.da.yml", "*.de.yml", "*.el.yml", "*.es.yml", "*.et.yml", "*.fa_IR.yml", "*.fi.yml", "*.fr.yml", "*.gl.yml", "*.he.yml", "*.hu.yml", "*.id.yml", "*.it.yml", "*.ja.yml", "*.ko.yml", "*.lt.yml", "*.lv.yml", "*.nb_NO.yml", "*.nl.yml", "*.pl_PL.yml", "*.pt.yml", "*.pt_BR.yml", "*.ro.yml", "*.ru.yml", "*.sk.yml", "*.sl.yml", "*.sq.yml", "*.sr.yml", "*.sv.yml", "*.sw.yml", "*.te.yml", "*.th.yml", "*.tr_TR.yml", "*.uk.yml", "*.ur.yml", "*.vi.yml", "*.zh_CN.yml", "*.zh_TW.yml"]
    },
    {
      "path": "db",
      "file_exclude_patterns": ["*.sqlite3"]
    },
    { "path": "lib" },
    { "path": "plugins" },
    { "path": "script" },
    { "path": "spec" },
    { "path": "vendor" },
    { "path": "test" }
  ],
  "settings":
  {
    // indentation
    "tab_size": 2,
    "translate_tabs_to_spaces": true,
    "trim_trailing_white_space_on_save": true,
    // ensure line endings is linux style (even when on Windows)
    "default_line_ending": "LF"
  }
}

import DiscourseRecommended from "@discourse/lint-configs/eslint";

export default [
  ...DiscourseRecommended,
  {
    rules: {
      "ember/no-classic-classes": "error",
      "discourse/i18n-import-location": "error",
      "discourse/i18n-t": "error",
      "qunit/no-assert-equal-boolean": "error",
      "qunit/no-assert-equal": "error",
      "qunit/no-loose-assertions": "error",
      "qunit/no-negated-ok": "error",
      "qunit/no-ok-equality": "error",
    },
  },
  {
    ignores: [
      "app/assets/javascripts/ember-addons/",
      "lib/javascripts/locale/*",
      "lib/javascripts/messageformat.js",
      "lib/javascripts/messageformat-lookup.js",
      "plugins/**/lib/javascripts/locale",
      "public/",
      "vendor/",
      "app/assets/javascripts/discourse/tests/fixtures",
      "**/node_modules/",
      "spec/",
      "app/assets/javascripts/discourse/dist/",
      "tmp/",
    ],
  },
];
