module.exports = {
    helpers: {
        random: () => Math.floor(3000 + (5000 - 3000) * Math.random()),
    },
};

{
	"folders":
	[
		{
			"name": "Monorepo Root",
			"path": ".",
			"folder_exclude_patterns": [
				"//apps/meteor",
				"//packages",
				"//ee/apps",
				"//ee/packages"
			],
		},
		{
			"name": "Rocket.Chat",
			"path": "./apps/meteor",
		},
		{
			"name": "Packages",
			"path": "./packages",
		},
		{
			"name": "Enterprise Apps",
			"path": "./ee/apps",
		},
		{
			"name": "Enterprise Packages",
			"path": "./ee/packages",
		}
	],
	"settings": {
		"LSP": {
			"LSP-typescript": {
				"enabled": true,
				"disabled_capabilities": {
					// Uncomment the line below to load auto complete suggestions from sublime's index instead of typescript.
					// The index is much faster, but doesn't filter out results based on the current context.

					// "completionProvider": true,
				},
			},
			"LSP-eslint": {
				"settings": {
					// If node path is not automatically detected, you can specify it here (use the global eslint settings if you have a global node)
					// "nodePath": null
					"workingDirectories": [
						{
							"mode": "auto",
						}
					],
				},
			},
		},
	},
}

{
  "name": "Rocket.Chat",
  "description": "Have your own open-source Slack-like online chat platform.",
  "repository": "https://github.com/RocketChat/Rocket.Chat",
  "logo": "https://raw.githubusercontent.com/RocketChat/Rocket.Chat.Artwork/master/Logos/2020/png/logo-horizontal-red.png",
  "keywords": ["meteor", "social", "community", "chat"],
  "website": "https://rocket.chat",
  "env": {
    "BUILDPACK_URL": "https://github.com/RocketChat/meteor-buildpack-horse.git",
    "METEOR_APP_DIR": "apps/meteor/",
    "HEROKU_APP_NAME": {
      "description": "Please re-enter your App Name from the top.",
      "required": true
    },
    "DEPLOY_PLATFORM": "heroku"
  },
  "addons": [
    "mongolab",
    "logentries"
  ]
}

module.exports = {
	plugins: {
		autoprefixer: {},
		'postcss-css-variables': {
			preserve: true,
		},
		'postcss-selector-not': {},
		'postcss-flexbugs-fixes': {},
		'postcss-ie11-supports': {},
		'postcss-logical': {},
		'postcss-dir-pseudo-class': {},
		cssnano: {},
	},
};

{
	"extends": "./tsconfig.base.json",
	"compilerOptions": {
		"target": "es2015",
		"lib": ["dom", "dom.iterable", "esnext"],
		"module": "esnext",
		"declaration": true,
		"declarationMap": true,
		"sourceMap": true,
		"jsx": "react-jsx",
	},
	"exclude": ["node_modules", "**/*.spec.ts"]
}

{
	"extends": "./tsconfig.base.json",
	"compilerOptions": {
		"target": "es2020",
		"lib": ["es2020"],
		"module": "commonjs",
		"sourceMap": true,
	},
	"exclude": ["node_modules", "**/*.spec.ts"],
	"ts-node": {
		"transpileOnly": true
	}
}

{
	"$schema": "https://turborepo.org/schema.json",
	"globalDependencies": ["tsconfig.base.json", "tsconfig.base.server.json", "tsconfig.base.client.json"],
	"tasks": {
		"build-preview": {
			"dependsOn": ["^build"],
			"outputs": ["storybook-static/**", ".storybook/**", "dist/**"]
		},
		".:build-preview-move": {
			"dependsOn": ["^build-preview"],
			"cache": false
		},
		"build": {
			"dependsOn": ["^build"],
			"outputs": ["dist/**"]
		},
		"testunit": {
			"dependsOn": ["build"],
			"outputs": ["coverage/**"]
		},
		"lint": {
			"dependsOn": ["build"],
			"outputs": []
		},
		"translation-check": {
			"outputs": []
		},
		"typecheck": {
			"dependsOn": ["^build"],
			"outputs": []
		},
		"dev": {
			"dependsOn": ["^dev"],
			"cache": false
		},
		"dsv": {
			"dependsOn": ["build"],
			"cache": false
		},
		"ms": {
			"dependsOn": ["^build"]
		},
		"@rocket.chat/meteor#build": {
			"dependsOn": ["^build"],
			"cache": false
		},
		"@rocket.chat/meteor#build:ci": {
			"dependsOn": ["^build"],
			"cache": false
		}
	}
}
