    products: [
        .library(name: "Fastlane", targets: ["Fastlane"])
    ],
    dependencies: [
        .package(url: "https://github.com/kareman/SwiftShell", .upToNextMajor(from: "5.1.0"))
    ],
    targets: [
        .target(
            name: "Fastlane",
            dependencies: ["SwiftShell"],
            path: "./fastlane/swift",
