      'react/jsx-no-bind': 'off',
      'lines-between-class-members': 'off',
      'max-classes-per-file': 'off',
      'react/react-in-jsx-scope': 'off',
      'no-else-return': 'off',
      'import/export': 'off',
      'consistent-return': 'off',
      'no-param-reassign': [
        'error',
        {
          props: true,
          ignorePropertyModificationsFor: ['prev', 'acc'], // ignore for Array.reduce
        },
      ],

      'max-len': [
        'warn',
