          code: 140,
        },
      ],

      '@typescript-eslint/return-await': 'off',
      '@typescript-eslint/no-base-to-string': 'error',

      'no-restricted-imports': [
        'error',
        {
          patterns: [noRestrictedImportsMultiLevelNovuPattern],
        },
      ],

      'padding-line-between-statements': [
        'error',
        {
          blankLine: 'any',
          prev: ['const', 'let', 'var'],
          next: ['if', 'for'],
        },
        {
