{
  "root": true,
  "ignorePatterns": ["website/**/*", "__fixtures__"],
  "plugins": ["@nx"],
  "settings": {
    "import/resolver": {
      "node": {
        "extensions": [".js", ".ts"]
      }
    }
  },
  "overrides": [
    {
      "files": ["*.ts", "*.tsx", "*.js", "*.jsx"],
      "rules": {
        "@nx/enforce-module-boundaries": [
          "error",
          {
            "enforceBuildableLibDependency": true,
            "allow": [],
            "depConstraints": [
              {
                "sourceTag": "*",
                "onlyDependOnLibsWithTags": ["*"]
              }
            ],
            "checkDynamicDependenciesExceptions": [".*"]
          }
        ],
        "no-restricted-imports": [
          "error",
          {
            "name": "npmlog",
            "message": "Please use the inlined core utility instead."
          }
        ]
      }
    },
    {
      "files": ["*.ts", "*.tsx"],
      "extends": ["plugin:@nx/typescript"],
      "rules": {
        "@typescript-eslint/no-empty-function": "off",
        "@typescript-eslint/no-explicit-any": "off",
        "@typescript-eslint/no-extra-semi": "error",
        "no-extra-semi": "off"
      }
    },
    {
      "files": ["*.js", "*.jsx"],
      "extends": ["airbnb-base", "plugin:node/recommended", "plugin:@nx/javascript"],
      "rules": {
        "@typescript-eslint/no-empty-function": "off",
        "class-methods-use-this": [
          "error",
          {
            "exceptMethods": ["defaultOptions", "otherCommandConfigs", "requiresGit"]
          }
        ],
        "consistent-return": "off",
        "curly": ["error", "all"],
        "no-param-reassign": [
          "error",
          {
            "props": true,
            "ignorePropertyModificationsFor": ["err", "obj", "pkg"]
          }
        ],
        "no-restricted-syntax": ["error", "ForInStatement", "LabeledStatement", "WithStatement"],
        "no-underscore-dangle": [
          "error",
          {
            "allowAfterThis": true
          }
        ],
        "no-use-before-define": [
          "error",
          {
            "functions": false,
            "classes": false
          }
        ],
        "node/no-unsupported-features/es-syntax": "off",
        "prefer-destructuring": "off",
        "prefer-object-spread": "off",
        "strict": "off",
        "default-param-last": "warn",
        "max-len": "off",
        "arrow-body-style": "off",
        "@typescript-eslint/no-extra-semi": "error",
        "no-extra-semi": "off"
      }
    },
    {
      "files": ["*.spec.ts", "*.spec.tsx", "*.spec.js", "*.spec.jsx", "*.test.js"],
      "extends": ["plugin:jest/recommended"],
      "env": {
        "jest": true
      },
      "rules": {}
    },
    {
      "files": "commands/*/command.js",
      "rules": {
        "global-require": "off"
      }
    },
    {
      "files": ["integration/**", "helpers/**", "**/__tests__/**", "**/__mocks__/**", "**/__helpers__/**"],
      "env": {
        "jest": true
      },
      "rules": {
        "import/no-extraneous-dependencies": "off",
        "import/order": "off",
        "max-classes-per-file": "off",
        "node/no-extraneous-require": "off",
        "node/no-unpublished-require": "off",
        "no-console": "off"
      }
    },
    {
      "files": ["test/fixtures/**", "**/__fixtures__/**"],
      "rules": {
        "global-require": "off",
        "import/no-extraneous-dependencies": "off",
        "import/no-unresolved": "off",
        "import/no-useless-path-segments": "off",
        "node/no-extraneous-require": "off",
        "node/no-missing-require": [
          "error",
          {
            "allowModules": ["@integration/package-1", "@integration/package-2"]
          }
        ],
        "no-console": "off",
        "strict": "off"
      }
    },
    {
      "files": ["utils/map-to-registry/lib/map-to-registry.js", "utils/npm-conf/lib/nerf-dart.js"],
      "rules": {
        "node/no-deprecated-api": "off"
      }
    },
    {
      "files": ["utils/npm-conf/lib/conf.js"],
      "rules": {
        "no-useless-catch": "off"
      }
    },
    {
      "files": "*.json",
      "parser": "jsonc-eslint-parser",
      "rules": {}
    }
  ]
}

import { getJestProjectsAsync } from "@nx/jest";

export default async () => ({
  projects: await getJestProjectsAsync(),
});

// eslint-disable-next-line import/no-extraneous-dependencies, node/no-unpublished-require
const nxPreset = require("@nx/jest/preset").default;

module.exports = {
  ...nxPreset,
  clearMocks: true,
  modulePathIgnorePatterns: ["/__fixtures__/", "<rootDir>/dist/"],
  testEnvironment: "node",
  /* TODO: Update to latest Jest snapshotFormat
   * By default Nx has kept the older style of Jest Snapshot formats
   * to prevent breaking of any existing tests with snapshots.
   * It's recommend you update to the latest format.
   * You can do this by removing snapshotFormat property
   * and running tests with --update-snapshot flag.
   * Example: "nx affected --targets=test,run-e2e-tests,integration --update-snapshot"
   * More info: https://jestjs.io/docs/upgrading-to-jest29#snapshot-format
   */
  snapshotFormat: { escapeString: true, printBasicPrototype: true },
};

{
  "$schema": "packages/lerna/schemas/lerna-schema.json",
  "command": {
    "create": {
      "homepage": "https://github.com/lerna/lerna",
      "license": "MIT"
    },
    "publish": {
      "tempTag": true
    },
    "version": {
      "conventionalCommits": true,
      "createRelease": "github",
      "exact": true,
      "message": "chore(release): %s"
    }
  },
  "ignoreChanges": ["**/__fixtures__/**", "**/__tests__/**", "**/*.md"],
  "version": "8.1.9"
}

{
  "$schema": "./node_modules/nx/schemas/nx-schema.json",
  "namedInputs": {
    "default": ["{projectRoot}/**/*", "sharedGlobals"],
    "sharedGlobals": [
      {
        "runtime": "node -e 'console.log(`${process.platform}-${process.arch}`)'"
      },
      {
        "runtime": "node --version"
      },
      {
        "runtime": "npm --version"
      },
      "{workspaceRoot}/.github/workflows/ci.yml",
      "{workspaceRoot}/.github/workflows/other-node-versions.yml"
    ],
    "production": [
      "default",
      "!{projectRoot}/**/?(*.)+(spec|test).[jt]s?(x)?(.snap)",
      "!{projectRoot}/tsconfig.spec.json",
      "!{projectRoot}/jest.config.[jt]s"
    ]
  },
  "targetDefaults": {
    "test": {
      "inputs": [
        "default",
        "^production",
        "{workspaceRoot}/jest.preset.js",
        "{workspaceRoot}/jest.config.ts"
      ],
      "dependsOn": ["build"],
      "cache": true,
      "options": {
        "passWithNoTests": true
      },
      "configurations": {
        "ci": {
          "ci": true,
          "codeCoverage": true
        }
      }
    },
    "lint": {
      "inputs": ["default", "{workspaceRoot}/.eslintrc.json", "{workspaceRoot}/.eslintignore"],
      "cache": true
    },
    "build": {
      "inputs": ["production", "^production"],
      "dependsOn": ["^build"],
      "cache": true
    },
    "compile": {
      "inputs": ["production", "^production"],
      "cache": true
    },
    "e2e": {
      "executor": "nx:run-commands",
      "options": {
        "commands": [
          {
            "command": "npm run e2e-start-local-registry"
          },
          {
            "command": "npm run e2e-build-package-publish"
          },
          {
            "command": "E2E_ROOT=$(npx ts-node tools/scripts/set-e2e-root.ts) nx run-e2e-tests {projectName}"
          }
        ],
        "parallel": false
      },
      "cache": true
    },
    "run-e2e-tests": {
      "inputs": [
        "default",
        "^production",
        "{workspaceRoot}/jest.preset.js",
        "{workspaceRoot}/jest.config.ts"
      ],
      "executor": "@nx/jest:jest",
      "options": {
        "jestConfig": "{projectRoot}/jest.config.ts",
        "passWithNoTests": true,
        "runInBand": true
      },
      "outputs": ["{workspaceRoot}/coverage/{projectRoot}"],
      "cache": true
    },
    "run-e2e-tests-process": {
      "inputs": [
        "default",
        "^production",
        "{workspaceRoot}/jest.preset.js",
        "{workspaceRoot}/jest.config.ts"
      ],
      "executor": "nx:run-commands",
      "options": {
        "commands": [
          {
            "command": "E2E_ROOT=$(npx ts-node tools/scripts/set-e2e-root.ts) nx run-e2e-tests {projectName}",
            "description": "This additional wrapper target exists so that we can ensure that the e2e tests run in a dedicated process with enough memory"
          }
        ],
        "parallel": false
      },
      "cache": true
    },
    "integration": {
      "inputs": ["default"],
      "dependsOn": ["^build"],
      "cache": true
    }
  },
  "nxCloudAccessToken": "MGQ4ZjkyMWYtYTVhNi00ZWQ4LWE0OTctNTkxM2U0ZDUzYWQ1fHJlYWQ="
}

