# default.nix
let
  pkgs = import <nixpkgs> { };
in
  pkgs.haskellPackages.developPackage {
    root = ./.;
    modifier = drv:
      pkgs.haskell.lib.addBuildTools drv (with pkgs.haskellPackages;
        [ cabal-install
          ghcid
          happy
        ]);
  }

{
  "nodes": {
    "flake-utils": {
      "inputs": {
        "systems": "systems"
      },
