{
  "lintSemverRanges": false,
  "lintFormatting": false,
  "semverGroups": [],
  "versionGroups": [
    {
      "dependencies": ["@types/**", "@testing-library/**", "typescript"],
      "dependencyTypes": ["prod"],
      "isBanned": true,
      "label": "These packages should only be under devDependencies."
    },
    {
      "dependencies": ["@refinedev/**"],
      "packages": ["**"],
      "isIgnored": true,
      "label": "@refinedev dependencies are managed by changeset."
    },
    {
      "dependencies": ["**"],
      "dependencyTypes": ["peer"],
      "packages": ["**"],
      "isIgnored": true
    },
    {
      "dependencies": ["nock"],
      "dependencyTypes": ["dev"],
      "packages": ["@refinedev/appwrite", "@refinedev/graphql"],
      "isIgnored": true
    },
    {
      "dependencies": ["next", "react", "react-dom", "@types/react", "@types/react-dom"],
      "packages": ["with-nextjs-headless"],
      "isIgnored": true
    }
  ]
}

{
  "$schema": "./node_modules/@biomejs/biome/configuration_schema.json",
  "organizeImports": {
    "enabled": false
  },
  "files": {
    "ignore": [
      "theme.d.ts",
      "documentation/plugins",
      "documentation/static",
      "package.json"
    ]
  },
  "formatter": {
    "enabled": true,
    "indentStyle": "space",
    "indentWidth": 2,
    "lineWidth": 80
  },
  "javascript": {
    "jsxRuntime": "reactClassic",
    "formatter": {
      "arrowParentheses": "always",
      "quoteStyle": "double",
      "semicolons": "always",
      "trailingComma": "all"
    }
  },
  "json": {
    "parser": {
      "allowComments": false,
      "allowTrailingCommas": false
    }
  },
  "linter": {
    "enabled": true,
    "rules": {
      "recommended": true,
      "a11y": {
        "noAriaHiddenOnFocusable": "off",
        "noAutofocus": "off",
        "noBlankTarget": "error",
        "noNoninteractiveElementToInteractiveRole": "off",
        "noNoninteractiveTabindex": "off",
        "noPositiveTabindex": "off",
        "noSvgWithoutTitle": "off",
        "useAltText": "off",
        "useButtonType": "off",
        "useHtmlLang": "off",
        "useIframeTitle": "off",
        "useKeyWithClickEvents": "off",
        "useKeyWithMouseEvents": "off",
        "useValidAnchor": "off"
      },
      "complexity": {
        "noBannedTypes": "off",
        "noExtraBooleanCast": "error",
        "noForEach": "off",
        "noStaticOnlyClass": "error",
        "noUselessCatch": "error",
        "noUselessConstructor": "error",
        "noUselessFragments": "off",
        "noUselessRename": "error",
        "noUselessSwitchCase": "error",
        "useArrowFunction": "error",
        "useLiteralKeys": "off",
        "useOptionalChain": "error",
        "noExcessiveNestedTestSuites": "off"
      },
      "correctness": {
        "noConstantCondition": "error",
        "noEmptyPattern": "error",
        "noSwitchDeclarations": "error",
        "noUnreachable": "error",
        "noUnsafeOptionalChaining": "error",
        "useExhaustiveDependencies": "off",
        "useJsxKeyInIterable": "off"
      },
      "performance": {
        "noAccumulatingSpread": "off",
        "noDelete": "off"
      },
      "security": {
        "noDangerouslySetInnerHtml": "error"
      },
      "style": {
        "noCommaOperator": "error",
        "noNonNullAssertion": "off",
        "noParameterAssign": "off",
        "noUnusedTemplateLiteral": "error",
        "noUselessElse": "error",
        "useConst": "error",
        "useDefaultParameterLast": "off",
        "useSelfClosingElements": "error",
        "useSingleVarDeclarator": "error",
        "useTemplate": "error",
        "useImportType": "error",
        "useNodejsImportProtocol": "off"
      },
      "suspicious": {
        "noArrayIndexKey": "off",
        "noAssignInExpressions": "error",
        "noAsyncPromiseExecutor": "error",
        "noConfusingVoidType": "off",
        "noControlCharactersInRegex": "error",
        "noDoubleEquals": "error",
        "noDuplicateJsxProps": "error",
        "noDuplicateObjectKeys": "error",
        "noEmptyInterface": "error",
        "noExplicitAny": "off",
        "noFallthroughSwitchClause": "error",
        "noGlobalIsNan": "error",
        "noImplicitAnyLet": "off",
        "noPrototypeBuiltins": "error",
        "noRedeclare": "error",
        "noRedundantUseStrict": "error",
        "noSelfCompare": "error",
        "noShadowRestrictedNames": "off",
        "useDefaultSwitchClauseLast": "error",
        "useValidTypeof": "error"
      }
    }
  },
  "overrides": [
    {
      "include": ["packages/ui-tests"],
      "linter": {
        "rules": {
          "suspicious": {
            "noExportsInTest": "off",
            "useDefaultSwitchClauseLast": "error"
          }
        }
      }
    }
  ],
  "vcs": {
    "clientKind": "git",
    "defaultBranch": "main",
    "enabled": true,
    "useIgnoreFile": true
  }
}

module.exports = {
  extends: ["@commitlint/config-conventional"],
  rules: {
    "header-max-length": [1, "always", 160],
    "body-max-line-length": [1, "always", 160],
  },
  helpUrl:
    "https://refine.dev/docs/guides-concepts/contributing/#committing-your-work-and-preparing-a-pull-request",
  ignores: [
    (commit) => commit.includes("Optimised images with calibre/image-actions"),
  ],
};

module.exports = {
  projects: ["<rootDir>/packages/*/jest.config.js"],
  coverageDirectory: "<rootDir>/coverage/",
};

{
  "tasksRunnerOptions": {
    "default": {
      "runner": "nx/tasks-runners/default",
      "options": { "cacheableOperations": ["build", "types", "lint", "test"] }
    }
  },
  "targetDefaults": {
    "build": {
      "dependsOn": ["^build"],
      "outputs": [
        "{projectRoot}/.next",
        "{projectRoot}/build",
        "{projectRoot}/dist"
      ],
      "cache": true
    },
    "lint": {
      "cache": true
    },
    "test": {
      "cache": true,
      "outputs": ["{projectRoot}/coverage"]
    },
    "dev": {
      "dependsOn": ["^build"],
      "outputs": ["{projectRoot}/dist"]
    },
    "types": {
      "dependsOn": ["^types", "build"],
      "outputs": ["{projectRoot}/dist"]
    },
    "attw": {
      "dependsOn": ["build"]
    },
    "publint": {
      "dependsOn": ["build"]
    },
    "start": {
      "dependsOn": ["build"]
    }
  },
  "defaultProject": "@refinedev/core",
  "cli": {
    "packageManager": "pnpm"
  },
  "workspaceLayout": {
    "libsDir": "packages",
    "appsDir": "examples"
  },
  "namedInputs": {
    "default": ["{projectRoot}/**/*", "sharedGlobals"],
    "sharedGlobals": [],
    "production": ["default"]
  },
  "nxCloudAccessToken": "NGI2NjNiYWItMWNhYi00MDA4LWI1OTMtM2IwYmFmYzRkMjEzfHJlYWQtd3JpdGU=",
  "$schema": "./node_modules/nx/schemas/nx-schema.json",
  "defaultBase": "main"
}

{
  "extends": "./tsconfig.build.json",
  "include": ["packages", "example"],
  "compilerOptions": {
    "allowJs": false,
    "baseUrl": "."
  }
}
