{
  "name": "codemirror",
  "version": "5.65.18",
  "main": "lib/codemirror.js",
  "style": "lib/codemirror.css",
  "author": {
    "name": "Marijn Haverbeke",
    "email": "marijn@haverbeke.berlin",
    "url": "http://marijnhaverbeke.nl"
  },
  "description": "Full-featured in-browser code editor",
  "license": "MIT",
  "directories": {
    "lib": "./lib"
  },
  "scripts": {
    "build": "rollup -c",
    "watch": "rollup -w -c",
    "prepare": "npm run-script build",
    "test": "node ./test/run.js",
    "lint": "bin/lint"
  },
  "devDependencies": {
    "@rollup/plugin-buble": "^0.21.3",
    "blint": "^1.1.2",
    "cm5-vim": "^0.0.5",
    "node-static": "0.7.11",
    "puppeteer": "^1.20.0",
    "rollup": "^1.26.3",
    "rollup-plugin-copy": "^3.4.0"
  },
  "bugs": "http://github.com/codemirror/CodeMirror/issues",
  "keywords": [
    "JavaScript",
    "CodeMirror",
    "Editor"
  ],
  "homepage": "https://codemirror.net/5/",
  "repository": {
    "type": "git",
    "url": "https://github.com/codemirror/CodeMirror.git"
  },
  "jspm": {
    "directories": {},
    "dependencies": {},
    "devDependencies": {}
  },
  "dependencies": {},
  "publishConfig": {
    "tag": "version5"
  }
}

import buble from '@rollup/plugin-buble';
import copy from 'rollup-plugin-copy'

let copyVim = copy({
  targets: [
    { 
      src: require.resolve("cm5-vim/vim.js").replace(/\\/g,  "/"), 
      dest: "./keymap" 
    }
  ]
});

export default [
  {
    input: "src/codemirror.js",
    output: {
      banner: `// CodeMirror, copyright (c) by Marijn Haverbeke and others
// Distributed under an MIT license: https://codemirror.net/5/LICENSE

// This is CodeMirror (https://codemirror.net/5), a code editor
// implemented in JavaScript on top of the browser's DOM.
//
// You can find some technical background for some of the code below
// at http://marijnhaverbeke.nl/blog/#cm-internals .
`,
      format: "umd",
      file: "lib/codemirror.js",
      name: "CodeMirror"
    },
    plugins: [ buble({namedFunctionExpressions: false}), copyVim ]
  },
  {
    input: ["src/addon/runmode/runmode-standalone.js"],
    output: {
      format: "iife",
      file: "addon/runmode/runmode-standalone.js",
      name: "CodeMirror",
      freeze: false, // IE8 doesn't support Object.freeze.
    },
    plugins: [ buble({namedFunctionExpressions: false}) ]
  },
  {
    input: ["src/addon/runmode/runmode.node.js"],
    output: {
      format: "cjs",
      file: "addon/runmode/runmode.node.js",
      name: "CodeMirror",
      freeze: false, // IE8 doesn't support Object.freeze.
    },
    plugins: [ buble({namedFunctionExpressions: false}) ]
  },
];
